import React, { Component } from "react";
// import CustomHeader from "./Components/CustomHeader/CustomHeader";
// import MainContent from "./Components/MainContent/MainContent";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      message: "Hello!",
      newTodo: "",
      todos: [
        { title: "Learn React", done: false },
        { title: "Learn React2", done: false }
      ]
    };
  }

  newTodoChange(event) {
    console.log(event.target.value);
    this.setState({
      newTodo: event.target.value
    });
  }

  formSubmitted(event) {
    event.preventDefault();
    console.log(this.state.newTodo);
    this.setState({
      newTodo: "",
      todos: [
        ...this.state.todos,
        {
          title: this.state.newTodo,
          done: false
        }
      ]
    });
    // let todosArr = this.state.todos;
    // let newTodo = this.state.newTodo;
    // todosArr.push({ title: newTodo, done: false });
    // this.setState({ todos: todosArr });
  }

  toggleTodoDone(event, index) {
    const todos = [...this.state.todos]; // Copy todos array value
    // todos[index] = { ...todos[index] }; // Copy the specific object in the array
    todos[index].done = event.target.checked; // update the 'done' property of the object
    this.setState({
      todos
    });
    console.log(todos);
  }

  removeTodo(index) {
    const todos = [...this.state.todos]; // Copy todos array value
    todos.splice(index, 1); // remove from array
    this.setState({
      todos
    });
    console.log(todos);
  }

  allDone() {
    const todos = this.state.todos.map(todo => {
      return {
        ...todo,
        done: true
      };
    });
    this.setState({
      todos
    });
  }

  render() {
    return (
      <div className="App">
        {/* <CustomHeader />
        <MainContent /> */}
        <h1>{this.state.message}</h1>
        <form onSubmit={event => this.formSubmitted(event)}>
          <label htmlFor="newTodo">New Todo </label>
          <input
            id="newTodo"
            name="newTodo"
            onChange={event => this.newTodoChange(event)}
            value={this.state.newTodo}
          />
        </form>
        <button onClick={() => this.allDone()}>All Done</button>
        <ul>
          {this.state.todos.map((todo, index) => {
            return (
              <li key={index}>
                <input
                  type="checkbox"
                  onChange={event => this.toggleTodoDone(event, index)}
                  checked={todo.done}
                />
                <span
                  // style={{
                  //   textDecoration: todo.done ? "line-through" : "inherit"
                  // }}
                  className={todo.done ? "todo-done" : ""}
                >
                  {todo.title}
                </span>
                <button onClick={() => this.removeTodo(index)}>&#10005;</button>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default App;
