import React from "react";
import TaskItem from "./TaskItem";
import "./MainContent.scss";

const TaskList = props => {
  return (
    <div>
      <div className="sub-menu">
        <ul>
          <li onClick={() => props.checkAll()}>
            {props.allDone ? "Uncheck All" : "All Done"}
          </li>
          <li onClick={() => props.viewStatus("Show All")}>Show All</li>
          <li onClick={() => props.viewStatus("Active")}>Active</li>
          <li onClick={() => props.viewStatus("Done")}>Done</li>
        </ul>
      </div>
      <div className="task-container">
        {props.todos.map((todo, index) => {
          return (
            <TaskItem
              view={props.view}
              toggleTodoDone={props.toggleTodoDone}
              removeTodo={props.removeTodo}
              todo={todo}
              index={index}
              key={index}
            />
          );
        })}
      </div>
    </div>
  );
};

export default TaskList;
