import React, { Component } from "react";
import InputTextBox from "../../Components/TodoApp/InputTextBox";
import TaskList from "../../Components/TodoApp/TaskList";
import { connect } from "react-redux";
import "../../Components/TodoApp/MainContent.scss";
import { actions } from "../../store";

class TodoApp extends Component {
  //Add new TODO Task
  formSubmitted(event) {
    event.preventDefault();
    this.props.onAddTodo({
      title: this.props.newTodo,
      done: false
    });
  }

  toggleTodoDone(event, index) {
    const todos = [...this.state.todos]; // Copy todos array value
    // todos[index] = { ...todos[index] }; // Copy the specific object in the array
    todos[index].done = event.target.checked; // update the 'done' property of the object
    this.setState({
      todos
    });
  }

  removeTodo(index) {
    const todos = [...this.state.todos]; // Copy todos array value
    todos.splice(index, 1); // remove from array
    this.setState({
      todos
    });
    console.log(todos);
  }

  allDone() {
    const todos = this.state.todos.map(todo => {
      return {
        ...todo,
        done: true
      };
    });

    this.setState({
      todos
    });
  }
  render() {
    return (
      <div className="main-content">
        <div>
          <h2>
            {this.props.activePage}
            <InputTextBox
              newTodo={this.props.newTodo}
              formSubmitted={event => this.formSubmitted(event)} //Other way to bind
              newTodoChange={this.props.onNewTodoChanged} //Other way to bind
              activePage={this.props.activePage}
            />
          </h2>
          <TaskList
            toggleTodoDone={this.toggleTodoDone.bind(this)} //Simplest way to bind
            removeTodo={this.removeTodo.bind(this)} //Simplest way to bind
            todos={this.props.todos}
            checkAll={() => this.allDone()}
          />
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    activePage: state.activePage,
    newTodo: state.newTodo,
    todos: state.todos
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onNewTodoChanged(newTodo) {
      dispatch(actions.newTodoChanged(newTodo));
    },
    onAddTodo(todo) {
      dispatch(actions.addTodo(todo));
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TodoApp);
