import React from "react";
import TaskItem from "./TaskItem";
import "./MainContent.scss";

const TaskList = props => {
  return (
    <div>
      <div className="sub-menu">
        <span className="checkall" onClick={() => props.checkAll()}>
          {props.allDone ? "Uncheck All" : "All Done"}
        </span>
        <span className="dropdown">
          View
          <div className="dropdown-content">
            <li onClick={() => props.view("Show All")}>Show All</li>
            <li onClick={() => props.view("Active")}>Active</li>
            <li onClick={() => props.view("Done")}>Done</li>
          </div>
        </span>
      </div>
      <div className="task-container">
        {props.todos.map((todo, index) => {
          return (
            <TaskItem
              toggleTodoDone={props.toggleTodoDone}
              removeTodo={props.removeTodo}
              todo={todo}
              index={index}
              key={index}
            />
          );
        })}
      </div>
    </div>
  );
};

export default TaskList;
